/**
 * function to reactivate trigger
 */
function _retrigger()
{
	if($('.program').length && $('.program').val())
	{
		$('.program').trigger('change')
	}
	
	if($('.sumber_dana').length && $('.sumber_dana').val())
	{
		$('.sumber_dana').trigger('change')
	}
	
	if($('input[name=pilihan]').length)
	{
		$('input[name=pilihan][checked]').trigger('click')
	}
	
	if($('.model-isu').length && $('.model-isu').val())
	{
		$('.model-isu').trigger('change')
	}
	
	if($('.model_pilihan').length && $('.model_pilihan').val())
	{
		$('.model_pilihan').trigger('change')
	}
	
	if($('.jenis-anggaran').length && $('.jenis-anggaran').val())
	{
		$('.jenis-anggaran').trigger('change')
	}
	
	if($('.jenis-anggaran').length && $('.jenis-anggaran').val())
	{
		$('.jenis-anggaran').trigger('change')
	}
	
	if($('.sum_input').length)
	{
		$('.sum_input').trigger('change')
	}
	
	if($('.autofocus').length)
	{
		$('.autofocus').trigger('focus')
	}
	
	if($('.merge-text-result').length)
	{
		$('.merge-text-result').trigger('change')
	}
}